import { Component, OnDestroy, OnInit } from '@angular/core';
import { Observable, Subscription } from 'rxjs';
import { CarsService } from '../service/cars.service';
import { Categories } from '../service/categories';


@Component({
  selector: 'app-showroom',
  templateUrl: './showroom.component.html',
  styleUrls: ['./showroom.component.css']
})
export class ShowroomComponent implements OnInit, OnDestroy {

  title!: string;
  // cars = [
  //   {
  //     "name": "Audi",
  //     "model": "A4",
  //     "year": 2019,
  //     "price": 30000,
  //     "image": "https://cdn.motor1.com/images/mgl/0AN2V/s1/2019-audi-a4.jpg"
  //   },
  //   {
  //     "name": "BMW",
  //     "model": "X5",
  //     "year": 2018,
  //     "price": 40000,
  //     "image": "https://cdn.motor1.com/images/mgl/0AN2V/s1/2019-audi-a4.jpg"
  //   },
  //   {
  //     "name": "Mercedes",
  //     "model": "C200",
  //     "year": 2017,
  //     "price": 50000,
  //     "image": "https://cdn.motor1.com/images/mgl/0AN2V/s1/2019-audi-a4.jpg"
  //   }
  // ]

  //Objet 
  cars = {};
  categories: String[] = [];
  Object = Object;

  subscription! : Subscription; 

  constructor(private service: CarsService) { }


  ngOnInit() {
    this.title = "Showroom";
    this.subscription = this.service.getAllClass().subscribe((data) => {
       this.categories = Object.keys(data);
      // console.log(data);
    });   
  }

  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

  displayVehicles(category: String){
    this.subscription = this.service.getVehiclesByClass(category).subscribe((data) => {
      this.cars = data;
      console.log(data);
    });
  }

}
